import React, { useEffect, useState } from 'react';
import "../App.css";

interface AboutData {
    aboutId: string;
    image: string; // This will hold the URL or Base64 string of the image
    description: string;
    languages: string[];
}

const About: React.FC = () => {
    const [aboutData, setAboutData] = useState<AboutData | null>(null);

    useEffect(() => {
        fetch("http://localhost:8080/api/about/1") // Adjust the ID as needed
            .then((response) => response.json())
            .then((data) => {
                // Assuming languages are returned as a comma-separated string
                const languagesArray = data.languages.split(',').map((lang: string) => lang.trim());
                setAboutData({ ...data, languages: languagesArray });
            })
            .catch((error) => {
                console.error("Error fetching about data:", error);
            });
    }, []);

    if (!aboutData) {
        return <p>Loading...</p>; // Show a loading message while fetching data
    }

    const flagMapping: { [key: string]: string } = {
        English: "data:image/png;base64,",
        French: "https://upload.wikimedia.org/wikipedia/en/c/c3/Flag_of_France.svg",
        Bulgarian: "https://upload.wikimedia.org/wikipedia/commons/9/9a/Flag_of_Bulgaria.svg",
    };

    return (
        <div className="about">
            <img
                src={aboutData.image} // Use the image URL from the fetched data
                alt="About Me"
                className="about-image"
            />
            <h1>About Me</h1>
            <p>{aboutData.description}</p>
            <h1>Languages</h1>
            <div className="languages">
                {aboutData.languages.map((language) => (
                    <div key={language} className="language-item">
                        <img
                            src={flagMapping[language] || ""}
                            alt={language}
                            className="flag"
                        />
                        <p>{language}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default About;
